mapper.update:
	UPDATE A_notice
 	SET category=#{category}, title=#{title}, content=#{content}
 	WHERE num=#{num}

mapper.select:
SELECT result1.* FROM (SELECT num,category,title,content,viewCount, TO_CHAR(regdate, 'YYYY"년"MM"월"DD"일" HH24"시"MI"분"') AS regdate, LAG(num,1,0) OVER (ORDER BY num DESC) AS prevNum, LEAD(num,1,0) OVER (ORDER BY num DESC) AS nextNum FROM A_notice
WHERE title LIKE '%'||#{title}||'%' OR content Like '%'||#{content}||'%'
WHERE title LIKE '%'||#{title}||'%'
WHERE category LIKE '%'||#{category}||'%'
) result1 WHERE num=#{num}

mapper.update:
UPDATE A_notice
SET viewCount = viewCount+1
WHERE num=#{num}
